package com.alkyz.ratingcommenting.RatingInfo.Controller;import com.alkyz.ratingcommenting.RatingInfo.Entity.Rating;import com.alkyz.ratingcommenting.RatingInfo.RatingException;import com.alkyz.ratingcommenting.RatingInfo.Service.RatingService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping(path = "/ratings")public class RatingController {    private final RatingService ratingService;    @Autowired    public RatingController(RatingService ratingService) {        this.ratingService = ratingService;    }    @GetMapping    public List<Rating> getRatingInfo() {        return ratingService.getRating();    }    @GetMapping(path = "/sorted")    public List<Rating> getRatingInfoDesc() {        return ratingService.getRatingDesc();    }    @PostMapping("/add")    public void registerNewRatingInfo(@RequestBody Rating rating) throws RatingException {        ratingService.addNewRatingInfo(rating);    }    @GetMapping("/average")    public Double getAvgRating() {        return ratingService.getAverage();    }}